package variableAndDataType;

public class SecondClass {


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// age =32
		// name = Ashish
		// Marital status = true
		// height = 5.8
		// Gender =M
		
		// Primitive data-types: Byte, short, int , long, boolean, float, double, char 
		int age = 32;
		boolean maritalStatus =true;
		double height =5.8;
		char gender = 'M';
		
		// Not a primitive data-type -class, interface , array etc.
		String name = "ashish"; // String is not a data-type , it is a class.
		
		System.out.println(age);
		System.out.println(maritalStatus);
		System.out.println(height);
		System.out.println(name);
		System.out.println(gender);
		
		

	}

}
